arxiv = require('arxiv');

search_query = {
    category: 'cs*',
    author: 'Quoc V. Le'
};

arxiv.search(search_query, function(err, results) {
    console.log('Found ' + results.items.length + ' results out of ' + results.total);
    console.log(results.items[0].title);
    console.log(results.items[0].authors[0].name);
    return;

    // Print each entry's title
    for (var ei = 0; ei < results.items.length; ei++) {
        var entry = results.items[ei];
        console.log('[' + entry.published + '] ' + entry.title);

        // Print each author's name
        for (var ai = 0; ai < entry.authors.length; ai++) {
            var author = entry.authors[ai];
            console.log(' - ' + author.name);
        }
    }
});

